import "./routePage.scss"
import { Header } from "../../components/header/header"
import { useEffect, useState } from "react"
import { ProfileModal } from "../../components/profileModal/profileModal"
import array from "../../array.js"
export const RoutePage = ({
  profileModal,
  setProfileModal,
  textProfileModalActive,
  setTextProfileModalActive,
  routeUser,
}) => {
  const [routes, setRoutes] = useState([])
  const routeUserRoutePage = () => {
    const startIndex = routeUser.start // индекс начальной точки
    const endIndex = routeUser.end // индекс конечной точки

    let forward = []
    let backward = []

    // Прямой маршрут (с startIndex по endIndex)
    let i = startIndex
    while (i !== endIndex) {
      forward.push(...array[i].slice(1)) // добавляем все элементы, начиная с индекса 1 (пропуская название корпуса)
      i = (i + 1) % array.length // идем по циклу
    }

    // Обратный маршрут (с endIndex по startIndex)
    let j = endIndex
    while (j !== startIndex) {
      let prev = (j - 1 + array.length) % array.length // идем назад по циклу
      backward.push(...array[prev].slice(1)) // добавляем элементы
      j = prev
    }

    // Находим имена начальных и конечных точек
    const startName = array[startIndex][0].start
    const endName = array[endIndex][0].end

    // Теперь выводим маршруты в нужном порядке
    return [
      {
        direction: "Обратный маршрут",
        from: endName,
        to: startName,
        data: backward, // Обратный маршрут без переворота
      },
      {
        direction: "Прямой маршрут",
        from: startName,
        to: endName,
        data: forward,
      },
    ]
  }

  useEffect(() => {
    if (routeUser?.start != null && routeUser?.end != null) {
      const newRoutes = routeUserRoutePage()
      setRoutes(newRoutes)
    }
  }, [routeUser])

  return (
    <div className="routeePage">
      <Header setProfileModal={setProfileModal} />
      <div className="titleRoutePage">Ваш маршрут</div>
      <div className="BlockRoutePage">
        {routes.map((route, i) => (
          <div key={i}>
            <div className="directionTitle">{route.direction}</div>
            <div className="fromTo">
              {route.from} → {route.to}
            </div>
            {route.data.map((place, index) => (
              <div className="BlockRoutePage" key={index}>
                <div className="titlePlaceRoutePage">{place.name}</div>
                <div className="textPlaceRoutePage">{place.address}</div>
                <div className="textPlaceRoutePage">{place.info}</div>
                {/* <img src={`/img/${place.img}`} alt={place.name} /> */}
              </div>
            ))}
          </div>
        ))}
      </div>
      {profileModal && (
        <div className="profileModalRoutesPage">
          <ProfileModal
            textProfileModalActive={textProfileModalActive}
            setTextProfileModalActive={setTextProfileModalActive}
          />
        </div>
      )}
    </div>
  )
}
