import "./routePage.scss"
import { Header } from "../../components/header/header"
import { useEffect, useState } from "react"
import { ProfileModal } from "../../components/profileModal/profileModal"
import arr from "../../array.js"
export const RoutePage = ({
  profileModal,
  setProfileModal,
  textProfileModalActive,
  setTextProfileModalActive,
  routeUser,
}) => {
  const [route, setRoute] = useState([])
  const routeUserRoutePage = () => {
    const startIndex = routeUser.start
    const endIndex = routeUser.end

    let forward = []
    let i = startIndex
    while (i !== endIndex) {
      forward.push(...array[i % array.length].slice(1)) // пропускаем первый элемент с названиями корпусов
      i = (i + 1) % array.length
    }

    let backward = []
    i = startIndex
    while (i !== endIndex) {
      let prev = (i - 1 + array.length) % array.length
      backward.push(...array[prev].slice(1))
      i = prev
    }

    const startName = array[startIndex][0].start
    const endName =
      array[endIndex - 1 < 0 ? array.length - 1 : endIndex - 1][0].end

    if (forward.length < backward.length) {
      return [
        {
          direction: "Прямой маршрут",
          from: startName,
          to: endName,
          data: forward,
        },
      ]
    } else if (backward.length < forward.length) {
      return [
        {
          direction: "Обратный маршрут",
          from: endName,
          to: startName,
          data: backward,
        },
      ]
    } else {
      return [
        {
          direction: "Прямой маршрут",
          from: startName,
          to: endName,
          data: forward,
        },
        {
          direction: "Обратный маршрут",
          from: endName,
          to: startName,
          data: backward,
        },
      ]
    }
  }
  useEffect(() => {
    if (routeUser?.start != null && routeUser?.end != null) {
      const newRoute = routeUserRoutePage()
      setRoute(newRoute)
    }
  }, [routeUser])

  return (
    <div className="routeePage">
      <Header setProfileModal={setProfileModal} />
      <div className="titleRoutePage">Ваш маршрут</div>
      <div className="BlockRoutePage">
        <div className="titlePlaceRoutePage">Кронва</div>
        {route.data.map((place, index) => (
          <div className="BlockRoutePage" key={index}>
            <div className="titlePlaceRoutePage">{place.name}</div>
            <div className="textPlaceRoutePage">{place.address}</div>
            <div className="textPlaceRoutePage">{place.info}</div>
            {/* <img src={`/img/${place.img}`} alt={place.name} /> */}
          </div>
        ))}
      </div>
      {profileModal && (
        <div className="profileModalRoutesPage">
          <ProfileModal
            textProfileModalActive={textProfileModalActive}
            setTextProfileModalActive={setTextProfileModalActive}
          />
        </div>
      )}
    </div>
  )
}
