import "./routePage.scss"
import { Header } from "../../components/header/header"
import { useEffect, useState } from "react"
import { ProfileModal } from "../../components/profileModal/profileModal"
import array from "../../array.js"

export const RoutePage = ({
  profileModal,
  setProfileModal,
  textProfileModalActive,
  setTextProfileModalActive,
  routeUser,
}) => {
  const [routes, setRoutes] = useState([])

  const routeUserRoutePage = () => {
    const startIndex = routeUser.start
    const endIndex = routeUser.end
    const arrayLength = array.length

    // 1. Прямой маршрут (по часовой стрелке)
    let forwardData = []
    let forwardSteps = 0
    let current = startIndex

    while (current !== endIndex) {
      forwardData.push(...array[current].slice(1))
      current = (current + 1) % arrayLength
      forwardSteps++
    }

    // 2. Обратный маршрут (против часовой стрелки)
    let backwardData = []
    let backwardSteps = 0
    current = startIndex

    while (current !== endIndex) {
      current = (current - 1 + arrayLength) % arrayLength
      backwardData.push(...[...array[current].slice(1)].reverse()) // reverse: от end к start
      backwardSteps++
    }

    const startName = array[startIndex][0].start
    const endName = array[endIndex][0].start

    // 3. Сравнение по количеству шагов (корпусов)
    const chosen =
      forwardSteps <= backwardSteps
        ? {
            direction: "Прямой маршрут",
            from: startName,
            to: endName,
            data: forwardData,
          }
        : {
            direction: "Обратный маршрут",
            from: startName,
            to: endName,
            data: backwardData,
          }

    // Массив шагов для отображения промежуточных корпусов (не включая начальный и конечный)
    const steps = []
    const routeData = chosen.data
    let currentBuilding = null
    let places = []

    // Создаем шаги для промежуточных корпусов
    routeData.forEach((place, index) => {
      const buildingName = place.start
      if (currentBuilding && currentBuilding !== buildingName) {
        // Если это новый корпус, то добавляем предыдущий корпус в steps
        if (currentBuilding !== startName && currentBuilding !== endName) {
          steps.push({ building: currentBuilding, places })
        }
        places = [] // Очищаем список объектов для нового корпуса
      }
      currentBuilding = buildingName
      places.push(place)
    })

    // Добавляем последний корпус в steps, если он не начальный и не конечный
    if (
      currentBuilding &&
      places.length > 0 &&
      currentBuilding !== startName &&
      currentBuilding !== endName
    ) {
      steps.push({ building: currentBuilding, places })
    }

    return { ...chosen, steps }
  }

  useEffect(() => {
    if (routeUser?.start != null && routeUser?.end != null) {
      const newRoutes = routeUserRoutePage()
      setRoutes([newRoutes]) // Обновляем состояние с маршрутом
    }
  }, [routeUser])

  return (
    <div className="routeePage">
      <Header setProfileModal={setProfileModal} />
      <div className="titleRoutePage">Ваш маршрут</div>
      <div className="BlockRoutePage">
        {routes.map((route, i) => (
          <div key={i}>
            <div className="directionTitle">{route.direction}</div>
            <div className="fromTo">
              {route.from} → {route.to}
            </div>
            {/* Отображение маршрута с промежуточными корпусами */}
            {route.steps.map((step, index) => (
              <div key={index}>
                <div className="titlePlaceRoutePage">{step.building}</div>
                {step.places.map((place, idx) => (
                  <div className="BlockRoutePage" key={idx}>
                    <div className="titlePlaceRoutePage">{place.name}</div>
                    <div className="textPlaceRoutePage">{place.address}</div>
                    <div className="textPlaceRoutePage">{place.info}</div>
                    {/* <img src={`/img/${place.img}`} alt={place.name} /> */}
                  </div>
                ))}
              </div>
            ))}
          </div>
        ))}
      </div>
      {profileModal && (
        <div className="profileModalRoutesPage">
          <ProfileModal
            textProfileModalActive={textProfileModalActive}
            setTextProfileModalActive={setTextProfileModalActive}
          />
        </div>
      )}
    </div>
  )
}
