import "./routePage.scss"
import { Header } from "../../components/header/header"
import { useEffect, useState } from "react"
import { ProfileModal } from "../../components/profileModal/profileModal"
import array from "../../array.js"
export const RoutePage = ({
  profileModal,
  setProfileModal,
  textProfileModalActive,
  setTextProfileModalActive,
  routeUser,
}) => {
  const [routes, setRoutes] = useState([])
  const routeUserRoutePage = () => {
    const startIndex = routeUser.start
    const endIndex = routeUser.end
    const arrayLength = array.length

    const getSteps = (direction) => {
      let steps = []

      if (direction === "forward") {
        let current = startIndex
        while (true) {
          const buildingName = array[current][0].start
          const places = array[current].slice(1)
          steps.push({ building: buildingName, places })
          if (current === endIndex) break
          current = (current + 1) % arrayLength
        }
      } else {
        let tempSteps = []
        let current = startIndex
        while (true) {
          const buildingName = array[current][0].start
          const places = array[current].slice(1) // Без reverse!
          tempSteps.push({ building: buildingName, places })
          if (current === endIndex) break
          current = (current - 1 + arrayLength) % arrayLength
        }
        steps = tempSteps.reverse() // Чтобы путь шёл от старта к финишу
      }

      return steps
    }

    const forwardSteps = getSteps("forward")
    const backwardSteps = getSteps("backward")

    const chosen =
      forwardSteps.length <= backwardSteps.length
        ? {
            direction: "Прямой маршрут",
            steps: forwardSteps,
          }
        : {
            direction: "Обратный маршрут",
            steps: backwardSteps,
          }

    return [chosen]
  }
  useEffect(() => {
    if (routeUser?.start != null && routeUser?.end != null) {
      const newRoutes = routeUserRoutePage()
      setRoutes(newRoutes)
    }
  }, [routeUser])

  return (
    <div className="routeePage">
      <Header setProfileModal={setProfileModal} />
      <div className="titleRoutePage">Ваш маршрут</div>
      <div className="BlockRoutePage">
        {routes.map((route, i) => (
          <div key={i}>
            <div className="directionTitle">{route.direction}</div>
            {route.steps.map((step, index) => (
              <div key={index}>
                <div className="buildingTitle">
                  {index === 0
                    ? `Корпус отправления: ${step.building}`
                    : index === route.steps.length - 1
                    ? `Корпус прибытия: ${step.building}`
                    : `Промежуточный корпус: ${step.building}`}
                </div>
                {step.places.map((place, i) => (
                  <div className="BlockRoutePage" key={i}>
                    <div className="titlePlaceRoutePage">{place.name}</div>
                    <div className="textPlaceRoutePage">{place.address}</div>
                    <div className="textPlaceRoutePage">{place.info}</div>
                  </div>
                ))}
              </div>
            ))}
          </div>
        ))}
      </div>
      {profileModal && (
        <div className="profileModalRoutesPage">
          <ProfileModal
            textProfileModalActive={textProfileModalActive}
            setTextProfileModalActive={setTextProfileModalActive}
          />
        </div>
      )}
    </div>
  )
}
