import "./routePage.scss"
import { Header } from "../../components/header/header"
import { useEffect, useState } from "react"
import { ProfileModal } from "../../components/profileModal/profileModal"
export const RoutePage = ({
  profileModal,
  setProfileModal,
  textProfileModalActive,
  setTextProfileModalActive,
  routeUser,
}) => {
  const buildings = ["Кронва", "Чайка", "Лото", "Гривцова", "Биржа"]

  // массивы объектов между корпусами по кругу
  const paths = [
    ["Крона → Чайка"], // Кронва-Чайка
    ["Чайка → Лото"], // Чайка-Лото
    ["Лото → Гривцова"], // Лото-Гривцова
    ["Гривцова → Биржа"], // Гривцова-Биржа
    ["Биржа → Крона"], // Биржа-Кронва
  ]

  // функция получения пути между двумя корпусами
  function getPathData(start, end) {
    const startIndex = buildings.indexOf(start)
    const endIndex = buildings.indexOf(end)

    if (startIndex === -1 || endIndex === -1) return []

    // По часовой
    let forward = []
    let i = startIndex
    while (i !== endIndex) {
      forward.push(paths[i % paths.length])
      i = (i + 1) % buildings.length
    }

    // Против часовой
    let backward = []
    i = startIndex
    while (i !== endIndex) {
      let prev = (i - 1 + buildings.length) % buildings.length
      // здесь переворачиваем массив данных
      backward.push([...paths[prev]].reverse())
      i = prev
    }

    // Возвращаем либо прямой путь, либо перевёрнутый
    return forward.length <= backward.length ? forward : backward
  }

  return (
    <div className="routeePage">
      <Header setProfileModal={setProfileModal} />
      <div className="titleRoutePage">Ваш маршрут</div>
      <div className="BlockRoutePage">
        <div className="titlePlaceRoutePage">Кронва</div>
        <div className="textPlaceRoutePage">музей</div>
      </div>
      {profileModal && (
        <div className="profileModalRoutesPage">
          <ProfileModal
            textProfileModalActive={textProfileModalActive}
            setTextProfileModalActive={setTextProfileModalActive}
          />
        </div>
      )}
    </div>
  )
}
